import React from 'react';
import './Messages.css';
import ScrollToBottom from 'react-scroll-to-bottom';
const Messages = ({messages}) => {
    return (
        <ScrollToBottom>
            {messages.map((message,i) => <div key={i}>message</div>)}
        </ScrollToBottom>
    )
}


export default Messages;
